<script>
import { TweenMax, set, TimelineMax, Sine, Bounce, Elastic } from 'gsap';

export default {
  mounted() {
    TweenMax.set('#platform, #giftcontain', {
      visibility: 'visible'
    });
    TweenMax.set('.baghandle', {
      transformOrigin: '20% 50%'
    });

    function gift() {
      var tl = new TimelineMax();

      tl.add('start');
      tl.fromTo(
        '#gift',
        1,
        {
          y: -500
        },
        {
          y: 0,
          ease: Bounce.easeOut
        },
        'start+=1'
      );
      tl.fromTo(
        '#giftshadow',
        0.8,
        {
          scale: 0,
          opacity: 0
        },
        {
          scale: 1,
          opacity: 0.5,
          ease: Bounce.easeOut
        },
        'start+=1'
      );
      tl.fromTo(
        '#ribbon',
        0.5,
        {
          y: 0
        },
        {
          y: -50,
          ease: Elastic.easeOut.config(3, 0.3)
        },
        'start+=0.85'
      );
      tl.to(
        '#ribbon',
        0.5,
        {
          y: 0,
          ease: Elastic.easeOut.config(1.75, 0.3)
        },
        'start+=1.35'
      );
      tl.to(
        '#giftcontain',
        0.25,
        {
          opacity: 0,
          ease: Sine.easeIn
        },
        'start+=3'
      );
      tl.to(
        '#giftshadow',
        0.1,
        {
          opacity: 0,
          ease: Sine.easeIn
        },
        'start+=2.9'
      );

      return tl;
    }

    var master = new TimelineMax({ repeat: -1 });
    master.add(gift(), 'gift');
  }
};
</script>

